mapper.select:
select count(*)
 from Member_tb
  where memberid = #{memberid}

select memberid, name, password, email_id, email_domain, tel1, tel2, tel3 
 from Member_tb
  where memberid = #{memberid} and password = #{password}

select memberid, name, password, email_id, email_domain, tel1, tel2, tel3 	
from Member_tb
where memberid = #{memberid}
